INSERT INTO `content` (`content_id`, `title`, `author`, `content_date`, `sub_title`, `adult_tag`, `body`) VALUES
(1, 'এক যুগ আগে যেভাবে ভেস্তে গিয়েছিল তিস্তা চুক্তি', 'শুভজ্যোতি ঘোষ', '2023-09-03', '', 0, 'সালটা ২০১১। সেপ্টেম্বরের প্রথম সপ্তাহে ভারতের কেন্দ্রীয় মন্ত্রিসভার রুটিন বৈঠক ছিল সেটা।\r\n\r\nতবে দীর্ঘ বারো বছর পর ভারতের কোনও প্রধানমন্ত্রী ওই সপ্তাহেই দ্বিপাক্ষিক সফরে বাংলাদেশ যাচ্ছেন, তাই সেই সফর সংক্রান্ত বেশ কিছু গুরুত্বপূর্ণ বিষয়ও মিটিংয়ের এজেন্ডায় ছিল।\r\n\r\nসেই সব কাগজপত্রের বান্ডিলে তিস্তা চুক্তি সংক্রান্ত একটি খসড়া দেখেই খটকা লাগে ক্যাবিনেটে তৃণমূল কংগ্রেসের একমাত্র সদস্য, দেশের রেলমন্ত্রী দীনেশ ত্রিবেদীর।\r\n\r\nতৃণমূল তার মাত্র তিন-চার মাস আগেই ইতিহাস গড়ে পশ্চিমবঙ্গের ক্ষমতায় এসেছে, রাজ্যের মুখ্যমন্ত্রী হয়েছেন মমতা ব্যানার্জি।বৈঠকে মন্ত্রিসভার অনুমোদন চেয়ে চুক্তির যে খসড়াটি পেশ করা হয়েছিল, দীনেশ ত্রিবেদীর সন্দেহ হয়েছিল তা রাজ্য সরকারের সম্মতি ছাড়াই চূড়ান্ত করা হয়েছে।\r\n\r\nবিষয়টি নিয়ে বৈঠকে অর্থমন্ত্রী তথা কংগ্রেসের শীর্ষস্থানীয় নেতা প্রণব মুখার্জির সঙ্গে তীব্র বাদানুবাদেও জড়িয়ে পড়েন তিনি।\r\n\r\nঅর্থমন্ত্রী হলেও বাংলাদেশের প্রধানমন্ত্রীর সঙ্গে ব্যক্তিগত সুসম্পর্কের সূত্র ধরে সঙ্গে ভারতের হয়ে তিস্তা চুক্তির বিষয়টি মূলত মি. মুখার্জিই তখন দেখছিলেন।');
INSERT INTO `content` (`content_id`, `title`, `author`, `content_date`, `sub_title`, `adult_tag`, `body`) VALUES
(17, 'সামনে নির্বাচন, সময় খারাপ: ওবায়দুল কাদের', 'বিশেষ প্রতিনিধি ', '2023-09-06', 'সামনে জাতীয় সংসদ নির্বাচন। সময়টা খারাপ', 0, 'সামনে জাতীয় সংসদ নির্বাচন। সময়টা খারাপ। এ সময় অশুভ শক্তি সংখ্যালঘু সম্প্রদায়ের ওপর হামলা করতে পারে—এমন আশঙ্কা প্রকাশ করেছেন আওয়ামী লীগের সাধারণ সম্পাদক ওবায়দুল কাদের।\r\n\r\nআজ বুধবার বিকেলে রাজধানীর পলাশী চৌরাস্তায় কেন্দ্রীয় জন্মাষ্টমী উৎসব উপলক্ষে আয়োজিত শোভাযাত্রার আগে সংক্ষিপ্ত সমাবেশে প্রধান অতিথির বক্তব্যে সড়ক পরিবহন ও সেতুমন্ত্রী ওবায়দুল কাদের এই আশঙ্কার কথা বলেন।\r\n\r\nএ সময় মন্ত্রী আগামী দুর্গাপূজায় হিন্দুধর্মাবলম্বীদের জানমালের নিরাপত্তা রক্ষায় আইনশৃঙ্খলা রক্ষাকারী বাহিনীর পাশাপাশি অন্য ধর্মাবলম্বীদের এগিয়ে আসার আহ্বান জানান।  সংখ্যালঘুদের নিরাপত্তার বিষয়ে ওবায়দুল কাদের বলেন, ‘সময়টা খারাপ। এবার আমি ভয় পাচ্ছি যে অশুভ শক্তি বাংলাদেশের জাতীয় নির্বাচন সামনে রেখে এমন কোনো ঘটনা যদি ঘটায়, যেটা হিন্দুদের সাথে এবং প্রতিবেশী দেশ ভারতের সাথে আমাদের সম্পর্কের ফাটল ধরাতে পারে। রাজনৈতিক, সাম্প্রদায়িক, জঙ্গিবাদী, দুর্বৃত্তরা—এ ধরনের ঘটনা ঘটাতে পারে। কাজেই এবার বেশি করে সতর্ক থাকতে হবে।’\r\n\r\nমুসলমানসহ অন্য ধর্মাবলম্বীদের প্রতি আওয়ামী লীগের সাধারণ সম্পাদক বলেন, ‘শারদীয় দুর্গোৎসবে হিন্দু ভাইবোনদের পাশে থাকবেন। সারা দেশে দুর্গাপূজায় মণ্ডপ, হিন্দুদের বাড়িঘর, মন্দির যাতে অশুভ শক্তি ক্ষতি করতে না পারে, সে জন্য সবাইকে আহ্বান করব। হিন্দুদের পাশে দাঁড়াতে হবে। তাদের জীবনের, সম্পদের, বাড়িঘরের, মন্দিরের, মণ্ডপের নিরাপত্তা দিতে হবে।’ তিনি উল্লেখ করেন,  ‘আইনশৃঙ্খলা রক্ষাকারী বাহিনী সতর্ক অবস্থানে আছে। জনগণকেও সতর্ক থাকতে হবে। খুব স্পর্শকাতর সময়। তিন মাস পর জাতীয় নির্বাচন। এই নির্বাচন সামনে রেখে অশুভ শক্তির নানান খেলা আছে।\r\nওবায়দুল কাদের আরও বলেন, গত নির্বাচনে ড. কামাল হোসেনকে নিয়ে বিএনপি নির্বাচনের বিরুদ্ধে নিজেদের অশুভ তাণ্ডব চালিয়েছে। এবারও নোবেল বিজয়ী অর্থনীতিবিদ ইউনূসকে ইস্যু করে মাঠে নামতে চায়। সেই খেলা ইতিমধ্যে শুরু হয়ে গেছে। ইউনূসকে নিয়ে আবার এক-এগারোর অস্বাভাবিক সরকারের খেলায় মেতে উঠতে চায়।\r\n\r\nসংখ্যালঘুদের নিরাপত্তার একমাত্র গ্যারান্টি প্রধানমন্ত্রী শেখ হাসিনা উল্লেখ করে ওবায়দুল কাদের বলেন, ‘এসব অশুভ শক্তিকে নিয়ে বিএনপি অশুভ খেলা খেলতে চায়। কাজেই অসাম্প্রদায়িক, মানবতাবাদ ও গণতন্ত্রে বিশ্বাসীদের সরকারের পাশে দাঁড়াতে হবে। এই সরকারকে টিকিয়ে রাখতে হবে।’\r\n\r\nওবায়দুল কাদের উপস্থিত লোকদের উদ্দেশে বলেন, ‘দেশে অসুরের আস্ফালন হচ্ছে। দুষ্টের সংখ্যা বেড়ে গেছে।’ তিনি বলেন, গত সাড়ে ১৪ বছরে এটা প্রমাণ হয়েছে, শাসক হিসেবে প্রধানমন্ত্রী শেখ হাসিনার চেয়ে রাজনীতিতে সনাতন ধর্মের মানুষের আপন দ্বিতীয় কেউ নেই। আওয়ামী লীগ ক্ষমতায় থাকার সময় হিন্দু সম্প্রদায়ের ভাইবোনেরা নিরাপদে ছিলেন, নিরাপদে আছেন। এক বছর কুমিল্লাসহ কিছু স্থানে দু-তিনটি বিচ্ছিন্ন ঘটনা ঘটেছে।'),
(18, 'জমজমের পানি নিয়ে সৌদি আরবের নির্দেশনা', 'এএফপি কায়রো', '2021-09-03', 'সৌদি আরবে হজ ও ওমরাহ করতে যাওয়া মানুষদের জন্য নির্', 0, 'মক্কার গ্র্যান্ড মসজিদ ও মদিনার মসজিদে নববিতে জীবাণুমুক্ত পাত্রে মুসলিমদের জন্য জমজমের পানি রাখা হয়। সৌদি আরবের হজ ও ওমরাহ–সংক্রান্ত মন্ত্রণালয়ের নির্দেশনা অনুযায়ী, মুসলিমদের ধাক্কাধাক্কি এড়িয়ে চলা এবং পরোপকারী মনোভাব পোষণের পরামর্শ দেওয়া হয়েছে। পবিত্র এ পানি সংগ্রহের ক্ষেত্রে বয়স্করা অগ্রাধিকার পাবেন। এ ছাড়া নির্ধারিত জায়গায় পানি পানের পর কাপগুলো যথাস্থানে রাখতে বলা হয়েছে। জায়গাটিকে পরিষ্কার রাখার স্বার্থে মেঝেতে পানি ফেলা থেকে বিরত থাকার পরামর্শ দেওয়া হয়েছে।\r\nহজ শেষ হওয়ার পর সৌদি আরবে ওমরাহর মৌসুম শুরু হয়েছে। বিশ্বের মুসলিমদের মধ্যে যাঁরা শারীরিক কিংবা আর্থিক কারণে হজ করতে পারেন না, তাঁরা গ্র্যান্ড মসজিদে ওমরাহ করে থাকেন। বিশেষ করে বিদেশি হজ ও ওমরাহ যাত্রীদের কাছে জমজমের পানি জনপ্রিয়। দেশে ফেরার পর স্বজনদের উপহার হিসেবে দিতে তাঁরা জমজমের পানি কিনে থাকেন। ওমরাহ শেষ হওয়ার পর দেশে ফেরার আগে মুসলিমরা নুসুক অ্যাপের মাধ্যমে জমজমের পানির বোতল বুকিং দিয়ে থাকেন।'),
(19, 'শুক্র ও রোববার বিক্ষোভ মিছিল করবে জামায়াত', 'বিশেষ প্রতিনিধি', '2023-09-05', 'জামায়াতে ইসলামী আগামী শুক্র ও রোববার (৮ ও ১০ সেপ্ট', 0, 'শুক্রবার ঢাকা মহানগরে ও রোববার দেশের অন্যান্য মহানগরে বিক্ষোভ মিছিল করবে দলটি। আজ বুধবার সন্ধ্যায় এক বিবৃতিতে জামায়াতে ইসলামীর ভারপ্রাপ্ত সেক্রেটারি জেনারেল মাওলানা এ টি এম মা’ছুম এই কর্মসূচি ঘোষণা করেন।\r\n\r\nবিবৃতিতে বলা হয়, দলের সাবেক নেতা দেলাওয়ার হোসাইন সাঈদীর জানাজায় বাধা, বিভিন্ন স্থানে গায়েবানা জানাজায় হামলা, কয়েক শ নেতা-কর্মীকে গ্রেপ্তার, হাজার হাজার নেতা-কর্মীর বিরুদ্ধে মিথ্যা মামলা দায়েরের প্রতিবাদে এবং জামায়াতের আমির শফিকুর রহমানসহ গ্রেপ্তার নেতা-কর্মীদের মুক্তি ও তত্ত্বাবধায়ক সরকারের অধীনে নির্বাচনের দাবিতে এই কর্মসূচি দেওয়া হয়েছে।'),
(20, 'পার্বতীপুরে নীলসাগর এক্সপ্রেস ট্রেন লাইনচ্যুত, দুই ঘণ্টা চলাচল ব্যাহত', 'প্রতিনিধি সৈয়দপুর, নীলফামারী', '2023-09-21', 'দিনাজপুরের পার্বতীপুর উপজেলায় নীলসাগর এক্সপ্রেস ট্', 0, 'এতে প্রায় দুই ঘণ্টা ট্রেন চলাচল ব্যাহত হয়।\r\n\r\nআন্তনগর নীলসাগর এক্সপ্রেস ট্রেনটি ঢাকা থেকে চিলাহাটি যাওয়ার পথে ট্রেনের পেছন থেকে ২ নম্বর বগির সামনের চারটি চাকা লাইনচ্যুত হলে এ ঘটনা ঘটে। তবে এতে কেউ আহত হননি বলে জানা গেছে।\r\n\r\nএ ব্যাপারে জানতে চাইলে ট্রেনের এসএসফিটার হুমায়ুন কবির বলেন, হুইল মেজারমেন্ট ভেঙে যাওয়ার কারণে বগিটি লাইনচ্যুত হয়।ট্রেনের পরিচালক মো. সিফাত জানান, হুইল মেজারমেন্টর বিষয়টি ৫ দিন আগে বঙ্গবন্ধু সেতুর পূর্ব পাশে তাঁদের নজরে আসে। গত রাতে চিলাহাটি থেকে ঢাকা যাওয়ার পথে সান্তাহার রেলস্টেশনে ক্যারেজ ফিটাররা এসে তা ঠিক করে যান। এদিকে ট্রেনটি জয়পুরহাট থেকে বিরামপুর স্টেশনে পৌঁছানোর পর ঊর্ধ্বতন কর্মকর্তাদের বিষয়টি জানানো হয়। বিভাগীয় মেকানিক্যাল ইঞ্জিনিয়ারকে জানালে তিনি ৬০ কিলোমিটার স্পিডে গাড়ি চালিয়ে যেতে বলেন।\r\n\r\nলোকোমোটিভ মাস্টার মেহেদী হাসান বলেন, ট্রেনের স্পিড পার্বতীপুর-সান্তাহার স্টেশনের মধ্যে ৯০ কিলোমিটার হলেও তিনি নির্দেশনা পেয়ে ৬০ কিলোমিটার গতিতে ট্রেন চালিয়ে নিয়ে আসেন। পার্বতীপুর স্টেশনে প্রবেশের সময় ট্রেন লাইনচ্যুত হয়। পরে তিনটি বগি কেটে রেখে ট্রেনটি স্টেশনে নিয়ে যাওয়া হয়।\r\n\r\nপার্বতীপুর রেলওয়ে স্টেশনমাস্টার রেজাউল করিম মুঠোফোনে বলেন, ট্রেনটির তিনটি কোচের হুইল মেজারমেন্ট ভেঙে পড়ে। ট্রেনটি ঝুঁকিপূর্ণ অবস্থায় পার্বতীপুর রেলওয়ে স্টেশনে প্রবেশ করে। কিন্তু এর আগেই ট্রেনের তিনটি কোচ লাইনচ্যুত হয়। ফলে তাঁরা ওই কোচ তিনটি খুলে রাখার সিদ্ধান্ত নেন। দেড় ঘণ্টা পর তিনটি বগি কেটে রেখে ট্রেনটি চিলাহাটি অভিমুখে ছেড়ে যায়। ট্রেনে গতি কম থাকায় দুর্ঘটনায় কোনো প্রকার ক্ষতি হয়নি বলে জানান তিনি।'),
(21, 'নিরুত্তাপ ভেনিসে উত্তাপ ছড়ালেন রোনালদোর প্রেমিকা', 'রোনালদোর প্রেমিকা', '2023-09-04', '', 1, 'চলছে ভেনিস আন্তর্জাতিক চলচ্চিত্র উৎসবের ৮০তম আসর তবে হলিউডের চলমান ধর্মঘটের কারণে এবার অনেকটা রং হারিয়েছে উৎসব। নেই বড় কোনো তারকা, উৎসব নিয়ে আলোচনাও যেন এবার একটু কম হচ্ছে। তবে নিরুত্তাপ ভেনিসে উত্তাপ ছড়িয়েছেন জর্জিনা রদ্রিগেজ। ');
